import './Projects.css'
import video from '../Photos/Untitled.mov'
import { useInView } from 'react-intersection-observer';


function Projects() {

  const { ref, inView } = useInView({
    /* Optional options */
    threshold: 0.4,
  });

  return (
    <div className='projects'>
      <div className= "project-one" ref={ ref }>
        {/* <video class='project-one-video' autoPlay loop muted>
          <source src={video} type="video/mp4"></source>
          Your browser does not support the video tag.
        </video> */}
      </div>
      <div className={inView ? "project-two project-zoom" : "project-two"} ref={ ref }>

      </div>
      <div className={inView ? "project-three project-zoom" : "project-three"} ref={ ref }>

      </div>
      <div className={inView ? "project-four project-zoom" : "project-four"} ref={ ref }>

      </div>
    </div>
  )
}

export default Projects
